mapper.select:
SELECT * FROM `order`

    is_deleted = 0

        AND id = #{params.id}


        AND (
        title LIKE CONCAT('%', #{params.keyword}, '%')
        OR title LIKE CONCAT('%', #{params.keyword}, '%')
        )


        AND client_id = #{params.clientId}


        AND status = #{params.status}


ORDER BY created_at DESC
