mapper.select:
SELECT * FROM `user`

    is_deleted = 0

        AND id = #{params.id}


        AND role = #{params.role}


        AND openid = #{params.openid}


        AND nickname LIKE CONCAT('%', #{params.nickname}, '%')


        AND gender = #{params.gender}


        AND age BETWEEN #{params.ageMin} AND #{params.ageMax}


ORDER BY created_at DESC

SELECT u.id, u.nickname, u.age, u.gender, u.avatar, us.bio, GROUP_CONCAT(st.name) AS service_types FROM user u LEFT JOIN user_servant us ON u.id = us.user_id LEFT JOIN user_servant_service_type usst ON us.id = usst.user_servant_id LEFT JOIN service_type st ON usst.service_type_id = st.id
1=1 AND u.role = 2 AND u.is_deleted = 0
AND u.id = #{params.id}
AND u.nickname LIKE CONCAT('%', #{params.nickname}, '%')
AND u.gender = #{params.gender}
AND u.age BETWEEN #{params.ageMin} AND #{params.ageMax}
AND usst.service_type_id = #{params.serviceType}
GROUP BY u.id, u.nickname, u.age, u.gender, u.avatar, us.bio ORDER BY u.created_at DESC
